mapper.select:
select

from t_finished_surface
where f_work_project_id = #{workProjectId,jdbcType=INTEGER}
and f_surface_id = #{surfaceId,jdbcType=INTEGER}
and f_is_delete = 0

mapper.insert:
insert into t_finished_surface (f_id, f_work_project_id, f_surface_id,
  f_type, f_name, f_is_delete, f_creator_id,
  f_createtime, f_updater_id, f_updatetime
  )
values (#{id,jdbcType=INTEGER}, #{workProjectId,jdbcType=INTEGER}, #{surfaceId,jdbcType=INTEGER},
  #{type,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{isDelete,jdbcType=BIT}, #{creatorId,jdbcType=INTEGER},
  #{createtime,jdbcType=TIMESTAMP}, #{updaterId,jdbcType=INTEGER}, #{updatetime,jdbcType=TIMESTAMP}
  )
