mapper.select:
select ttm.f_id, ttm.f_teaching_id, ttm.f_content, ttm.f_sender_id, ttm.f_sender_type, ttm.f_createtime, ttm.f_updatetime, ttm.f_is_delete, case when ttm.f_sender_type = 2 then tt.f_name when ttm.f_sender_type = 1 then ts.f_name end AS senderName from t_teaching_message ttm left join t_teacher tt on ttm.f_sender_id = tt.f_id left join t_student ts on ttm.f_sender_id = ts.f_id where ttm.f_is_delete = 0 and ttm.f_teaching_id = #{teachingId,jdbcType=INTEGER}
and ttm.f_id <= #{startId}
order by ttm.f_createtime desc
